    constructor(address _magaPrinter) {
        magaPrinter = IMagaPrinter(_magaPrinter);
    }

    function howMuchMAGA(address account) public view returns (uint256) {
        require(account != address(0), "Invalid address");
        return magaPrinter.getMagaAmount(account);
    }
}
