// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract FeeExclusion {
    // Mapping to store whether an address is excluded from fees
    mapping(address => bool) private excludedFromFees;

    /**
     * @dev Checks if an address is excluded from fees.
     * @param account The address to check.
     * @return True if the address is excluded from fees, false otherwise.
     */
    function isExcludedFromFees(address account) public view returns (bool) {
        require(account != address(0), "Invalid address");
        return excludedFromFees[account];
    }

    /**
     * @dev Internal function to set the fee exclusion status for an address.
     * @param account The address to update.
     * @param excluded The exclusion status to set.
     */
    function _setExcludedFromFees(address account, bool excluded) internal {
        require(account != address(0), "Invalid address");
        excludedFromFees[account] = excluded;
    }
}
