// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract WalletManager {
    address private owner;
    uint256 private maxWalletSize;

    event MaxWalletSizeUpdated(uint256 newMaxWalletSize);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    function setMaxWalletSize(uint256 newMaxWalletSize) public onlyOwner {
        maxWalletSize = newMaxWalletSize;
        emit MaxWalletSizeUpdated(newMaxWalletSize);
    }

    function getMaxWalletSize() public view returns (uint256) {
        return maxWalletSize;
    }

    function getOwner() public view returns (address) {
        return owner;
    }
}
```

