// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    uint256 public maxTxAmount;
    uint256 public taxSwapThreshold;
    uint256 public maxTaxSwap;
    uint256 public storeE; // Assuming this is some multiplier or rate
    mapping(address => uint256) public balances;
    mapping(address => bool) public isBlacklisted;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier notBlacklisted(address account) {
        require(!isBlacklisted[account], "Account is blacklisted");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function transfer(address recipient, uint256 amount) public notBlacklisted(msg.sender) notBlacklisted(recipient) {
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Exceeds the max transaction amount");

        uint256 taxAmount = (storeE * amount) / 100; // Assuming storeE is a percentage multiplier
        uint256 transferAmount = amount - taxAmount;

        require(balances[msg.sender] >= amount, "Insufficient balance");
        require(balances[msg.sender] - amount <= balances[msg.sender], "SafeMath: subtraction overflow");
        require(balances[recipient] + transferAmount >= balances[recipient], "SafeMath: addition overflow");

        // Deduct the full amount from sender
        balances[msg.sender] -= amount;

        // Add the transfer amount to the recipient
        balances[recipient] += transferAmount;

        // Handle tax
        if (taxAmount > 0) {
            require(taxAmount <= maxTaxSwap, "Exceeds the max tax swap amount");
            balances[address(this)] += taxAmount;
            emit Transfer(msg.sender, address(this), taxAmount);
        }

        emit Transfer(msg.sender, recipient, transferAmount);
    }

    function blacklistAddress(address account, bool value) external onlyOwner {
        isBlacklisted[account] = value;
    }

    function setMaxTxAmount(uint256 newMaxTxAmount) external onlyOwner {
        maxTxAmount = newMaxTxAmount;
    }

    function setTaxSwapThreshold(uint256 newThreshold) external onlyOwner {
        taxSwapThreshold = newThreshold;
    }

    function setMaxTaxSwap(uint256 newMaxTaxSwap) external onlyOwner {
        maxTaxSwap = newMaxTaxSwap;
    }

    function setStoreE(uint256 newStoreE) external onlyOwner {
        storeE = newStoreE;
    }

    function deposit() external payable onlyOwner {
        // Allow the owner to deposit funds into the contract
    }

    function withdraw(uint256 amount) external onlyOwner {
        require(address(this).balance >= amount, "Insufficient contract balance");
        payable(owner).transfer(amount);
    }
}
