pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    address public taxReceiver;
    address public weth;
    address public uniRouter;
    uint256 public sellTax;
    uint256 public sellTaxThreshold;
    uint256 public maxTransfer;
    bool public isTradingEnabled;
    bool public inSwap;

    mapping(address => uint256) public balances;
    mapping(address => bool) public isExcludedFromTax;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier tradingEnabled() {
        require(isTradingEnabled, "Trading is not enabled");
        _;
    }

    modifier notInSwap() {
        require(!inSwap, "Currently in swap");
        _;
    }

    constructor() {
        owner = msg.sender;
        isTradingEnabled = false;
        inSwap = false;
    }

    function enableTrading() external onlyOwner {
        isTradingEnabled = true;
    }

    function transfer(address recipient, uint256 amount) external tradingEnabled notInSwap {
        require(recipient != address(0), "Recipient address cannot be zero");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(balances[msg.sender] >= amount, "Insufficient balance");
        require(amount <= maxTransfer, "Transfer amount exceeds max limit");

        // Handle tax if applicable
        uint256 taxAmount = 0;
        if (!isExcludedFromTax[msg.sender] && !isExcludedFromTax[recipient]) {
            taxAmount = (amount * sellTax) / 100;
            require(taxAmount <= sellTaxThreshold, "Tax exceeds threshold");
        }

        uint256 transferAmount = amount - taxAmount;

        // Update balances
        balances[msg.sender] -= amount;
        balances[recipient] += transferAmount;

        if (taxAmount > 0) {
            balances[taxReceiver] += taxAmount;
        }

        emit Transfer(msg.sender, recipient, transferAmount);

        // Perform swap if necessary
        if (taxAmount > 0) {
            _swapTax(taxAmount);
        }
    }

    function _swapTax(uint256 taxAmount) private {
        inSwap = true;

        // Example swap logic (replace with actual implementation)
        require(address(uniRouter).code.length > 0, "Invalid router address");
        // Call swap function on the router (pseudo-code)
        // uniRouter.swapExactTokensForETH(taxAmount, ...);

        inSwap = false;
    }

    function setTaxParameters(uint256 _sellTax, uint256 _sellTaxThreshold) external onlyOwner {
        sellTax = _sellTax;
        sellTaxThreshold = _sellTaxThreshold;
    }

    function setMaxTransfer(uint256 _maxTransfer) external onlyOwner {
        maxTransfer = _maxTransfer;
    }

    function excludeFromTax(address account, bool excluded) external onlyOwner {
        isExcludedFromTax[account] = excluded;
    }

    function deposit() external payable {
        balances[msg.sender] += msg.value;
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }
}
