// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    uint256 public maxTxAmount;
    uint256 public taxSwapThreshold;
    uint256 public maxTaxSwap;
    uint256 public taxRate; // Represented as a percentage (e.g., 5 for 5%)
    mapping(address => uint256) public balances;
    mapping(address => bool) public isExcludedFromTax;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        maxTxAmount = 1000 * 10**18; // Example value
        taxSwapThreshold = 100 * 10**18; // Example value
        maxTaxSwap = 500 * 10**18; // Example value
        taxRate = 5; // 5% tax
    }

    function transfer(address recipient, uint256 amount) public {
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Transfer amount exceeds the maxTxAmount");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        uint256 taxAmount = 0;

        // Calculate tax if sender is not excluded
        if (!isExcludedFromTax[msg.sender]) {
            taxAmount = (amount * taxRate) / 100;
            require(taxAmount <= maxTaxSwap, "Tax amount exceeds maxTaxSwap");
            require(taxAmount >= taxSwapThreshold, "Tax amount below taxSwapThreshold");
        }

        uint256 transferAmount = amount - taxAmount;

        // Update balances
        balances[msg.sender] -= amount;
        balances[recipient] += transferAmount;

        // Handle tax
        if (taxAmount > 0) {
            balances[address(this)] += taxAmount;
        }

        emit Transfer(msg.sender, recipient, transferAmount);

        if (taxAmount > 0) {
            emit Transfer(msg.sender, address(this), taxAmount);
        }
    }

    function setMaxTxAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTxAmount = _maxTxAmount;
    }

    function setTaxRate(uint256 _taxRate) external onlyOwner {
        require(_taxRate <= 100, "Tax rate must be less than or equal to 100%");
        taxRate = _taxRate;
    }

    function setTaxSwapThreshold(uint256 _taxSwapThreshold) external onlyOwner {
        taxSwapThreshold = _taxSwapThreshold;
    }

    function setMaxTaxSwap(uint256 _maxTaxSwap) external onlyOwner {
        maxTaxSwap = _maxTaxSwap;
    }

    function excludeFromTax(address account, bool excluded) external onlyOwner {
        isExcludedFromTax[account] = excluded;
    }

    function deposit() external payable {
        balances[msg.sender] += msg.value;
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }
}
