// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract TradingContract {
    address public owner;
    bool public tradingOpen;
    address public routerAddress;
    address public WETHAddress;

    event TradingOpened();
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor(address _routerAddress, address _WETHAddress) {
        owner = msg.sender;
        routerAddress = _routerAddress;
        WETHAddress = _WETHAddress;
        tradingOpen = false;
    }

    function openTrading() external onlyOwner {
        require(!tradingOpen, "Trading is already open");

        tradingOpen = true;

        // Approve the router to spend the maximum amount of tokens
        uint256 maxApproval = type(uint256).max;
        emit Approval(address(this), routerAddress, maxApproval);

        // Emit event to signal trading is open
        emit TradingOpened();
    }

    function approveRouter(uint256 amount) external onlyOwner {
        require(routerAddress != address(0), "ERC20: approve to the zero address");
        emit Approval(address(this), routerAddress, amount);
    }

    function approveWETH(uint256 amount) external onlyOwner {
        require(WETHAddress != address(0), "ERC20: approve to the zero address");
        emit Approval(address(this), WETHAddress, amount);
    }
}
```

