pragma solidity ^0.8.0;

contract TradingLimits {
    address public owner;
    bool public tradingActive;

    // Modifier to restrict access to the owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender; // Set the contract deployer as the owner
        tradingActive = true; // Initialize trading as active
    }

    /**
     * @dev Function to remove trading limits. Only callable by the owner.
     * @return success Returns true if the operation is successful.
     */
    function removeLimits() public onlyOwner returns (bool success) {
        tradingActive = false; // Disable trading limits
        return true; // Indicate success
    }
}
