// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredToken {
    string public name = "RefactoredToken";
    string public symbol = "RFT";
    uint8 public decimals = 18;
    uint256 public totalSupply = 1_000_000 * (10 ** decimals);

    address public owner;
    bool public tradingActive = false;
    bool public transferDelayEnabled = false;
    uint256 public maxTransactionAmount;
    uint256 public lastLpBurnTime;
    uint256 public percentForLPBurn;
    bool public lpBurnEnabled = false;

    mapping(address => uint256) private balances;
    mapping(address => mapping(address => uint256)) private allowances;
    mapping(address => uint256) private lastTransferBlock;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier tradingEnabled() {
        require(tradingActive, "Trading is not active");
        _;
    }

    constructor() {
        owner = msg.sender;
        balances[owner] = totalSupply;
        emit Transfer(address(0), owner, totalSupply);
    }

    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }

    function transfer(address recipient, uint256 amount) public tradingEnabled returns (bool) {
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(balances[msg.sender] >= amount, "Transfer amount exceeds balance");

        if (transferDelayEnabled) {
            require(block.number > lastTransferBlock[msg.sender], "Transfer delay enabled. Only one transfer per block allowed.");
            lastTransferBlock[msg.sender] = block.number;
        }

        balances[msg.sender] -= amount;
        balances[recipient] += amount;

        emit Transfer(msg.sender, recipient, amount);
        return true;
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        require(spender != address(0), "Approve to the zero address");

        allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public tradingEnabled returns (bool) {
        require(sender != address(0), "Transfer from the zero address");
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(balances[sender] >= amount, "Transfer amount exceeds balance");
        require(allowances[sender][msg.sender] >= amount, "Transfer amount exceeds allowance");

        if (transferDelayEnabled) {
            require(block.number > lastTransferBlock[sender], "Transfer delay enabled. Only one transfer per block allowed.");
            lastTransferBlock[sender] = block.number;
        }

        balances[sender] -= amount;
        balances[recipient] += amount;
        allowances[sender][msg.sender] -= amount;

        emit Transfer(sender, recipient, amount);
        return true;
    }

    function enableTrading() external onlyOwner {
        tradingActive = true;
    }

    function setTransferDelayEnabled(bool enabled) external onlyOwner {
        transferDelayEnabled = enabled;
    }

    function setMaxTransactionAmount(uint256 amount) external onlyOwner {
        maxTransactionAmount = amount;
    }

    function burnLiquidity(uint256 amount) external onlyOwner {
        require(lpBurnEnabled, "Liquidity burn is not enabled");
        require(block.timestamp > lastLpBurnTime, "Liquidity burn cooldown active");

        uint256 burnAmount = (balances[address(this)] * percentForLPBurn) / 10000;
        require(burnAmount > 0, "Burn amount must be greater than zero");

        balances[address(this)] -= burnAmount;
        balances[address(0)] += burnAmount;

        lastLpBurnTime = block.timestamp;
        emit Transfer(address(this), address(0), burnAmount);
    }

    function enableLpBurn(bool enabled, uint256 percent) external onlyOwner {
        lpBurnEnabled = enabled;
        percentForLPBurn = percent;
    }
}
