// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    mapping(address => uint256) private balances;
    mapping(address => bool) private isSniperBlocked;
    bool public tradingActive;
    uint256 public swapTokensAtAmount;
    uint256 public sellTotalFees;
    uint256 public buyDevFee;
    address public owner;
    address public deadAddress = 0x000000000000000000000000000000000000dEaD;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        tradingActive = false;
    }

    function transfer(address recipient, uint256 amount) public {
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(msg.sender != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "ERC20: transfer amount must be greater than zero");
        require(tradingActive, "Trading is not active");
        require(!isSniperBlocked[msg.sender], "Sniper blocked");
        require(!isSniperBlocked[recipient], "Sniper blocked");

        // Check if sender has enough balance
        require(balances[msg.sender] >= amount, "ERC20: transfer amount exceeds balance");

        // Check if the contract's balance is below the swap threshold
        if (balances[address(this)] >= swapTokensAtAmount) {
            // Perform swap logic here (if applicable)
        }

        // Deduct the amount from sender and add to recipient
        balances[msg.sender] -= amount;
        balances[recipient] += amount;

        emit Transfer(msg.sender, recipient, amount);
    }

    function setTradingActive(bool _tradingActive) external onlyOwner {
        tradingActive = _tradingActive;
    }

    function setSwapTokensAtAmount(uint256 _swapTokensAtAmount) external onlyOwner {
        swapTokensAtAmount = _swapTokensAtAmount;
    }

    function setSellTotalFees(uint256 _sellTotalFees) external onlyOwner {
        sellTotalFees = _sellTotalFees;
    }

    function setBuyDevFee(uint256 _buyDevFee) external onlyOwner {
        buyDevFee = _buyDevFee;
    }

    function blockSniper(address account) external onlyOwner {
        isSniperBlocked[account] = true;
    }

    function unblockSniper(address account) external onlyOwner {
        isSniperBlocked[account] = false;
    }

    function balanceOf(address account) external view returns (uint256) {
        return balances[account];
    }

    function deposit() external payable {
        balances[msg.sender] += msg.value;
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }
}
