// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ERC20Token {
    mapping(address => uint256) private allowances;
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function decreaseAllowance(address spender, uint256 amount) public returns (bool) {
        require(spender != address(0), "ERC20: approve to the zero address");
        require(msg.sender != address(0), "ERC20: approve from the zero address");

        uint256 currentAllowance = allowances[msg.sender];
        require(currentAllowance >= amount, "ERC20: decreased allowance below zero");

        unchecked {
            allowances[msg.sender] = currentAllowance - amount;
        }

        emit Approval(msg.sender, spender, allowances[msg.sender]);
        return true;
    }
}
