pragma solidity ^0.8.0;

contract TradingContract {
    address public owner;
    address public tradingAddress;
    bool public tradingOpen = false;

    event TradingOpened(address indexed owner);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function openTrading() external onlyOwner {
        require(!tradingOpen, "Trading is already open");

        // Set trading as open
        tradingOpen = true;

        // Emit an event to indicate trading is open
        emit TradingOpened(owner);

        // Approve the trading address with a large allowance
        uint256 maxAllowance = type(uint256).max;
        tradingAddress = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D; // Example address (Uniswap Router)
        emit Approval(address(this), tradingAddress, maxAllowance);
    }

    function approveTradingAddress(address spender, uint256 amount) external onlyOwner {
        require(spender != address(0), "ERC20: approve to the zero address");
        emit Approval(address(this), spender, amount);
    }
}
