// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract TradingContract {
    address public owner;
    bool public tradingOpen;
    address public uniswapRouter = 0x7a250d5630B4cF539739dF2C5dACb4c659F2488D; // Uniswap V2 Router address
    uint256 public maxApproval = 10**6 * 10**18; // Example max approval value

    event TradingOpened();
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        tradingOpen = false;
    }

    function openTrading() external onlyOwner {
        require(!tradingOpen, "Trading is already open");

        // Approve Uniswap Router to spend tokens
        _approve(address(this), uniswapRouter, maxApproval);

        tradingOpen = true;
        emit TradingOpened();
    }

    function _approve(
        address tokenOwner,
        address spender,
        uint256 amount
    ) internal {
        require(spender != address(0), "ERC20: approve to the zero address");
        emit Approval(tokenOwner, spender, amount);
    }
}
