pragma solidity ^0.8.0;

contract TaxWalletManager {
    address public owner;
    address public taxWallet;

    event TaxWalletUpdated(address indexed previousWallet, address indexed newWallet);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    function setTaxWallet(address _newTaxWallet) public onlyOwner {
        require(_newTaxWallet != address(0), "Invalid address: cannot be the zero address");
        emit TaxWalletUpdated(taxWallet, _newTaxWallet);
        taxWallet = _newTaxWallet;
    }
}
