// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    bool public transferDelayEnabled;
    uint256 public maxTxAmount;
    uint256 public maxTaxSwap;
    uint256 public taxSwapThreshold;

    mapping(address => uint256) private lastTransferBlock;
    mapping(address => uint256) private balances;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier transferAllowed(address to, uint256 amount) {
        require(to != address(0), "Transfer to zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Exceeds the max transaction amount");
        require(block.number > lastTransferBlock[tx.origin], "Only one transfer per block allowed");
        _;
    }

    constructor() {
        owner = msg.sender;
        transferDelayEnabled = false;
        maxTxAmount = 1000 * 10**18; // Example value
        maxTaxSwap = 500 * 10**18; // Example value
        taxSwapThreshold = 100 * 10**18; // Example value
    }

    function transfer(address to, uint256 amount) public transferAllowed(to, amount) {
        address sender = msg.sender;

        // Ensure the sender has enough balance
        require(balances[sender] >= amount, "Insufficient balance");

        // Update the last transfer block for the sender
        lastTransferBlock[tx.origin] = block.number;

        // Perform the transfer
        balances[sender] -= amount;
        balances[to] += amount;

        emit Transfer(sender, to, amount);
    }

    function setTransferDelayEnabled(bool enabled) external onlyOwner {
        transferDelayEnabled = enabled;
    }

    function setMaxTxAmount(uint256 amount) external onlyOwner {
        maxTxAmount = amount;
    }

    function setMaxTaxSwap(uint256 amount) external onlyOwner {
        maxTaxSwap = amount;
    }

    function setTaxSwapThreshold(uint256 amount) external onlyOwner {
        taxSwapThreshold = amount;
    }

    function deposit() external payable {
        balances[msg.sender] += msg.value;
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }

    function getBalance(address account) external view returns (uint256) {
        return balances[account];
    }
}
