// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ERC20Token {
    // Mapping to store balances of addresses
    mapping(address => uint256) private balances;

    // Event to log transfers
    event Transfer(address indexed from, address indexed to, uint256 value);

    /**
     * @dev Transfers tokens from the caller's account to the specified address.
     * @param recipient The address to transfer tokens to.
     * @param amount The amount of tokens to transfer.
     * @return success A boolean indicating whether the transfer was successful.
     */
    function transfer(address recipient, uint256 amount) public returns (bool success) {
        // Ensure the recipient address is valid
        require(recipient != address(0), "ERC20: transfer to the zero address");

        // Ensure the sender has enough balance
        require(balances[msg.sender] >= amount, "ERC20: transfer amount exceeds balance");

        // Deduct the amount from the sender's balance
        balances[msg.sender] -= amount;

        // Add the amount to the recipient's balance
        balances[recipient] += amount;

        // Emit the Transfer event
        emit Transfer(msg.sender, recipient, amount);

        // Return true to indicate success
        return true;
    }

    /**
     * @dev Function to set initial balances for testing purposes.
     * This is not part of the ERC20 standard and is only for demonstration.
     * @param account The address to set the balance for.
     * @param amount The amount to set as the balance.
     */
    function setBalance(address account, uint256 amount) public {
        balances[account] = amount;
    }

    /**
     * @dev Function to get the balance of an account.
     * @param account The address to query the balance of.
     * @return The balance of the account.
     */
    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }
}
