// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EthereumMainnetUsage {
    // Event to log the frequency of Ethereum Mainnet usage
    event MainnetUsageFrequency(uint8 frequency);

    /**
     * @dev Emits an event with the frequency of Ethereum Mainnet usage.
     * @param frequency A number representing how often the user uses Ethereum Mainnet.
     *                  Must be a valid uint8 value.
     */
    function logMainnetUsageFrequency(uint8 frequency) public pure {
        // Ensure the input is a valid uint8 value
        require(frequency <= type(uint8).max, "Invalid frequency value");

        // Emit the event with the provided frequency
        emit MainnetUsageFrequency(frequency);
    }
}
