pragma solidity ^0.8.0;

contract AdminManager {
    address private admin;
    address private moderator;

    // Function to set the admin address
    function setAdmin(address newAdmin) public returns (bool) {
        // Ensure that only the moderator can call this function
        require(msg.sender == moderator, "Only the moderator can set the admin");

        // Update the admin address
        admin = newAdmin;

        // Return true to indicate success
        return true;
    }

    // Function to set the moderator address (for demonstration purposes)
    function setModerator(address newModerator) public {
        // This function can be restricted further as needed
        moderator = newModerator;
    }

    // Function to get the current admin address
    function getAdmin() public view returns (address) {
        return admin;
    }

    // Function to get the current moderator address
    function getModerator() public view returns (address) {
        return moderator;
    }
}
