// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    mapping(address => uint256) private balances;
    bool private tradingEnabled;
    uint256 private sellFees;
    address private storeC;
    address private storeE;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyWhenTradingEnabled() {
        require(tradingEnabled, "Trading is not started");
        _;
    }

    function transfer(address to, uint256 amount) public onlyWhenTradingEnabled {
        require(to != address(0), "Invalid address");
        require(amount > 0, "Amount must be greater than zero");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        balances[msg.sender] -= amount;
        balances[to] += amount;

        emit Transfer(msg.sender, to, amount);
    }

    function enableTrading() public {
        tradingEnabled = true;
    }

    function setSellFees(uint256 fees) public {
        sellFees = fees;
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        require(spender != address(0), "Invalid address");
        require(amount > 0, "Amount must be greater than zero");

        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function executeTrade(uint256 amount) public onlyWhenTradingEnabled {
        require(amount > 0, "Amount must be greater than zero");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        uint256 fee = (sellFees * amount) / 100;
        require(fee <= amount, "Fee exceeds amount");

        balances[msg.sender] -= amount;
        balances[storeC] += fee;
        balances[storeE] += (amount - fee);

        emit Transfer(msg.sender, storeC, fee);
        emit Transfer(msg.sender, storeE, amount - fee);
    }
}
