// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredToken {
    string public name = "Refactored Token";
    string public symbol = "RFT";
    uint8 public decimals = 18;
    uint256 public totalSupply;
    address public owner;
    uint256 public maxTxAmount;
    uint256 public sellTax;
    uint256 public buyTax;
    bool public tradingEnabled;
    bool public swapEnabled;

    mapping(address => uint256) private balances;
    mapping(address => mapping(address => uint256)) private allowances;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier tradingActive() {
        require(tradingEnabled, "Trading is not active");
        _;
    }

    constructor(uint256 _initialSupply, uint256 _maxTxAmount, uint256 _sellTax, uint256 _buyTax) {
        owner = msg.sender;
        totalSupply = _initialSupply * 10**decimals;
        balances[owner] = totalSupply;
        maxTxAmount = _maxTxAmount * 10**decimals;
        sellTax = _sellTax;
        buyTax = _buyTax;
        tradingEnabled = false;
        swapEnabled = false;
    }

    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        require(spender != address(0), "Approve to the zero address");
        allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transfer(address recipient, uint256 amount) public tradingActive returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public tradingActive returns (bool) {
        require(allowances[sender][msg.sender] >= amount, "Transfer amount exceeds allowance");
        allowances[sender][msg.sender] -= amount;
        _transfer(sender, recipient, amount);
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "Transfer from the zero address");
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Transfer amount exceeds the maxTxAmount");
        require(balances[sender] >= amount, "Transfer amount exceeds balance");

        uint256 taxAmount = 0;

        // Apply taxes if applicable
        if (swapEnabled) {
            if (recipient == owner) {
                taxAmount = (amount * sellTax) / 100;
            } else if (sender == owner) {
                taxAmount = (amount * buyTax) / 100;
            }
        }

        uint256 transferAmount = amount - taxAmount;

        balances[sender] -= amount;
        balances[recipient] += transferAmount;

        if (taxAmount > 0) {
            balances[owner] += taxAmount; // Send tax to the owner
            emit Transfer(sender, owner, taxAmount);
        }

        emit Transfer(sender, recipient, transferAmount);
    }

    function enableTrading() external onlyOwner {
        tradingEnabled = true;
    }

    function setMaxTxAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTxAmount = _maxTxAmount * 10**decimals;
    }

    function setTaxes(uint256 _sellTax, uint256 _buyTax) external onlyOwner {
        sellTax = _sellTax;
        buyTax = _buyTax;
    }

    function toggleSwap(bool _enabled) external onlyOwner {
        swapEnabled = _enabled;
    }
}
