// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract LiquidityBurner {
    address public owner;
    uint256 public lastManualLpBurnTime;
    uint256 public manualBurnFrequency;
    address public liquidityPairAddress;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event ManualNukeLP();

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor(address _liquidityPairAddress, uint256 _manualBurnFrequency) {
        owner = msg.sender;
        liquidityPairAddress = _liquidityPairAddress;
        manualBurnFrequency = _manualBurnFrequency;
    }

    function manualBurnLiquidityPairTokens(uint256 percentage) external onlyOwner returns (bool) {
        require(block.timestamp > lastManualLpBurnTime + manualBurnFrequency, "Must wait for cooldown to finish");
        require(percentage <= 10, "May not nuke more than 10% of tokens in LP");

        lastManualLpBurnTime = block.timestamp;

        uint256 lpBalance = IERC20(liquidityPairAddress).balanceOf(address(this));
        uint256 burnAmount = (lpBalance * percentage) / 100;

        require(burnAmount > 0, "Burn amount must be greater than zero");
        require(lpBalance >= burnAmount, "ERC20: transfer amount exceeds balance");

        IERC20(liquidityPairAddress).transfer(address(0xdead), burnAmount);

        emit Transfer(address(this), address(0xdead), burnAmount);
        emit ManualNukeLP();

        return true;
    }
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
