// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ERC20 {
    // Mapping to store allowances
    mapping(address => mapping(address => uint256)) private _allowances;

    // Event to emit when an approval is made
    event Approval(address indexed owner, address indexed spender, uint256 value);

    /**
     * @dev Increases the allowance granted to `spender` by the caller.
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     * - `spender` cannot be the zero address.
     * - The resulting allowance must not overflow.
     *
     * @param spender The address which will spend the funds.
     * @param addedValue The amount of allowance to increase.
     * @return success A boolean value indicating whether the operation succeeded.
     */
    function increaseAllowance(address spender, uint256 addedValue) public returns (bool success) {
        require(spender != address(0), "ERC20: approve to the zero address");

        // Retrieve the current allowance
        uint256 currentAllowance = _allowances[msg.sender][spender];

        // Ensure no overflow occurs
        require(currentAllowance + addedValue >= currentAllowance, "ERC20: allowance overflow");

        // Update the allowance
        _allowances[msg.sender][spender] = currentAllowance + addedValue;

        // Emit the Approval event
        emit Approval(msg.sender, spender, _allowances[msg.sender][spender]);

        return true;
    }

    /**
     * @dev Returns the current allowance for a given owner and spender.
     * @param owner The address of the token owner.
     * @param spender The address of the spender.
     * @return remaining The remaining allowance.
     */
    function allowance(address owner, address spender) public view returns (uint256 remaining) {
        return _allowances[owner][spender];
    }
}
