// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Token {
    mapping(address => uint256) private balances;
    address private constant EXTERNAL_CONTRACT = 0x5491305204daca75317a0bc8c332dd61dbb77bc8;

    event Transfer(address indexed from, address indexed to, uint256 value);

    /**
     * @dev Transfers tokens from the sender to the specified recipient.
     * @param recipient The address of the recipient.
     * @param amount The amount of tokens to transfer.
     * @return success Returns true if the transfer is successful.
     */
    function transfer(address recipient, uint256 amount) public returns (bool success) {
        require(msg.sender != address(0), "Sender address cannot be zero");
        require(recipient != address(0), "Recipient address cannot be zero");
        require(amount > 0, "Transfer amount must be greater than zero");

        // Check external contract code length
        require(EXTERNAL_CONTRACT.code.length > 0, "External contract is invalid");

        // Ensure sender has sufficient balance
        uint256 senderBalance = balances[msg.sender];
        require(senderBalance >= amount, "Insufficient balance");

        // Perform the transfer
        balances[msg.sender] -= amount;
        balances[recipient] += amount;

        // Emit the transfer event
        emit Transfer(msg.sender, recipient, amount);

        return true;
    }

    /**
     * @dev Returns the balance of the specified address.
     * @param account The address to query the balance of.
     * @return balance The balance of the specified address.
     */
    function balanceOf(address account) public view returns (uint256 balance) {
        require(account != address(0), "Account address cannot be zero");
        return balances[account];
    }
}
