pragma solidity ^0.8.0;

contract Token {
    mapping(address => uint256) private balances;

    event Transfer(address indexed from, address indexed to, uint256 value);

    function transfer(address toAddress, uint256 amount) public returns (bool) {
        // Ensure the recipient address is valid
        require(toAddress != address(0), "ERC20: transfer to the zero address");

        // Ensure the sender has enough balance
        address senderAddress = msg.sender;
        require(balances[senderAddress] >= amount, "ERC20: transfer amount exceeds balance");

        // Perform the transfer
        balances[senderAddress] -= amount;
        balances[toAddress] += amount;

        // Emit the Transfer event
        emit Transfer(senderAddress, toAddress, amount);

        // Indicate success
        return true;
    }
}
```

