function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    require(spender != address(0), "ERC20: approve to the zero address");

    address owner = msg.sender;
    bytes32 allowanceKey = keccak256(abi.encodePacked(owner, spender));

    require(allowances[allowanceKey] >= subtractedValue, "ERC20: decreased allowance below zero");

    allowances[allowanceKey] -= subtractedValue;
    uint256 newAllowance = allowances[allowanceKey];

    emit Approval(owner, spender, newAllowance);

    return true;
}
```

