        maxTransactionAmount = MAX_LIMIT;

        // Remove wallet size limit
        maxWalletSize = MAX_LIMIT;

        // Emit event for transaction limit update
        emit MaxTxAmountUpdated(MAX_LIMIT);
    }
}
