// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ExampleContract {
    address public owner;
    uint256 public maxTransactionAmount;
    uint256 public maxWalletSize;

    event MaxTxAmountUpdated(uint256 newMaxTxAmount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        maxTransactionAmount = 0;
        maxWalletSize = 0;
    }

    function removeLimits() public onlyOwner {
        uint256 defaultLimit = 0x02625a00; // Example default limit value

        // Update transaction amount limit
        maxTransactionAmount = defaultLimit;
        emit MaxTxAmountUpdated(defaultLimit);

        // Update wallet size limit
        maxWalletSize = defaultLimit;
    }
}
```

