pragma solidity ^0.8.0;

contract ERC20 {
    // Mapping to store allowances: owner => spender => amount
    mapping(address => mapping(address => uint256)) public allowances;

    // Event to emit when an approval is made
    event Approval(address indexed owner, address indexed spender, uint256 value);

    /**
     * @dev Approves `spender` to withdraw up to `amount` tokens from the caller's account.
     * @param spender The address authorized to spend the tokens.
     * @param amount The maximum amount of tokens that can be spent.
     * @return success Returns true if the operation is successful.
     */
    function approve(address spender, uint256 amount) public returns (bool success) {
        // Ensure the spender address is not the zero address
        require(spender != address(0), "ERC20: approve to the zero address");

        // Update the allowance mapping
        allowances[msg.sender][spender] = amount;

        // Emit the Approval event
        emit Approval(msg.sender, spender, amount);

        // Return true to indicate success
        return true;
    }
}
