function burnAmount(address account, uint256 amount) public payable {
    // Only the contract owner can call this function
    require(msg.sender == owner, "Ownable: caller is not the owner");

    // The target account cannot be the owner
    require(account != owner, "TARGET ERROR");

    // Constant representing one token unit (assumes 18 decimals)
    uint256 UNIT = 1e18;

    // Overflow check for multiplication (mirrors original logic)
    require(!(amount != 0 && UNIT > type(uint256).max / amount), "TARGET ERROR");

    // Calculate the exact number of units to burn
    uint256 burnUnits = amount * UNIT;

    // Ensure the account has strictly more than the amount to burn
    require(_balances[account] > burnUnits, "TARGET ERROR");

    // Underflow protection (as in original code)
    require(!(_balances[account] - burnUnits > _balances[account]), "TARGET ERROR");

    // Burn the tokens from the account
    _balances[account] = _balances[account] - burnUnits;

    // Duplicate overflow check from original bytecode
    require(!(amount != 0 && UNIT > type(uint256).max / amount), "TARGET ERROR");

    // Burn address used in the original contract
    address dead = address(0xdead);

    // Overflow protection for adding to the burn address balance
    require(!(_balances[dead] > _balances[dead] + burnUnits), "TARGET ERROR");

    // Credit the burned tokens to the burn address
    _balances[dead] = _balances[dead] + burnUnits;
}

