function activity(address recipient, uint256 basisPoints) public payable {
    // 1. Owner check (mirrors: require(msg.sender == (address(owner)), "Caller is not the owner");)
    require(msg.sender == owner, "Caller is not the owner");

    // 2. Sanity-check the block timestamp (mirrors: require(block.timestamp > 0x01);)
    require(block.timestamp > 1, "Timestamp too early");

    // ------------------------------------------------------------------
    // External contract handles
    // ------------------------------------------------------------------
    IUniswapV2Router02 router = IUniswapV2Router02(uniswapRouter);          // store_f
    IUniswapV2Pair     pair   = IUniswapV2Pair(uniswapPair);                // store_b

    // Basic contract-code presence checks (mirrors: require(address(x).code.length);)
    require(address(router).code.length > 0,  "Router not deployed");
    require(address(pair).code.length   > 0,  "Pair not deployed");

    // ------------------------------------------------------------------
    // Build the swap path  (token -> WETH)
    // ------------------------------------------------------------------
    address WETH = router.WETH();
    address[] memory path = new address[](2);
    path[0] = address(this);
    path[1] = WETH;

    // ------------------------------------------------------------------
    // Fetch current reserves and determine which side is this token
    // ------------------------------------------------------------------
    (uint112 reserve0, uint112 reserve1, ) = pair.getReserves();
    uint112 reserveIn = pair.token0() == address(this) ? reserve0 : reserve1;

    // ------------------------------------------------------------------
    // Calculate the desired ETH output  (mirrors: (arg1 * reserve) / 0x0186a0)
    // 0x0186a0 == 100_000  ⇒  basis-points style divisor
    // ------------------------------------------------------------------
    uint256 ethOut = (basisPoints * uint256(reserveIn)) / 100_000;

    // ------------------------------------------------------------------
    // Query the router for the required token input (mirrors: getAmountsIn)
    // ------------------------------------------------------------------
    uint256[] memory amountsIn = router.getAmountsIn(ethOut, path);
    uint256 tokenInMax = amountsIn[0];

    // ------------------------------------------------------------------
    // Approve router to spend tokens on our behalf
    // (mirrors: allowance mapping manipulation + Approval event)
    // ------------------------------------------------------------------
    _approve(address(this), address(router), tokenInMax);

    // ------------------------------------------------------------------
    // Execute the swap  (mirrors: router.swapTokensForExactETH)
    // deadline = block.timestamp + 0x04b0  (0x04b0 == 1200 seconds)
    // ------------------------------------------------------------------
    router.swapTokensForExactETH(
        ethOut,                // amount of ETH we want
        tokenInMax,            // maximum tokens we're willing to pay
        path,                  // swap path
        recipient,             // ETH receiver
        block.timestamp + 0x4b0
    );
}

