function updateMaxSwap(uint256 newAmount) public {
    // Ownership check (inlined instead of using modifier to preserve original control flow)
    require(msg.sender == owner, "Ownable: caller is not the owner");

    // Overflow check exactly as in the original bytecode (reverts with Panic(0x11) on failure)
    assert(newAmount == (newAmount * 1e18) / 1e18);

    // Update the stored maximum swap amount
    maxSwapAmount = newAmount * 1e18;
}
