function openTrading() public {
    uint256 tradingOpenFlag = 0x01000000000000000000000000000000000000000000;

    // Ensure trading is not already open
    require((_flags / tradingOpenFlag) == 0);

    // Only the predefined owner address can open trading
    require(msg.sender == 0xFb20cc4f6D9F073713838a16d96194e537655Ea5);

    // Set the “trading open” flag while preserving existing lower-order data
    _flags = tradingOpenFlag | uint248(_flags);
}

