function transfer(address recipient, uint256 amount) public returns (bool) {
    /*
     * Preconditions (mirrors the myriad `require` statements found in the
     * de-compiled output while keeping identical intent).
     */
    require(recipient == address(recipient), "Transfer amount exceeds the max transfer limit."); // de-compiler artefact
    require(recipient != address(0), "Transfer amount exceeds the max transfer limit.");         // `if (address(arg0))`
    require(msg.sender != address(0), "Transfer amount exceeds the max transfer limit.");        // `if (address(msg.sender))`
    require(amount > 0,              "Transfer amount exceeds the max transfer limit.");         // `if (arg1 > 0)`

    /*
     * Anti-whale logic.
     * The original bytecode gated the threshold check behind a convoluted
     * boolean flag.  
     * We conservatively replicate it without altering the logical outcome.
     */
    if (AntiWhaleEnabled) {
        require(
            !_isAntiWhaleExempt[msg.sender] &&
            !_isAntiWhaleExempt[recipient] &&
            amount <= antiWhaleThreshold,
            "Transfer amount exceeds the max transfer limit."
        );
    }

    /*
     * Balance bookkeeping (storage slot `storage_map_e` in the de-compiled
     * output corresponds to `_balances` here).
     *
     * NOTE: the de-compiled code ultimately subtracted the full `amount`
     *       from the sender and—after an inoperative `(0*amount)/1000`
     *       calculation—added the *same* amount to the recipient.
     *       Therefore, the effective fee is always zero.  
     *       We reproduce that exact behaviour.
     */
    uint256 senderBalance = _balances[msg.sender];
    require(senderBalance >= amount, "Transfer amount exceeds the max transfer limit.");
    unchecked { _balances[msg.sender] = senderBalance - amount; }

    // Fee is effectively zero in the de-compiled logic.
    uint256 feeAmount      = (0 * amount) / 1000; // keeps the original redundant arithmetic
    uint256 amountReceived = amount - feeAmount;

    if (feeAmount > 0) {
        _balances[address(this)] += feeAmount;
        emit Transfer(msg.sender, address(this), feeAmount);
    }

    _balances[recipient] += amountReceived;
    emit Transfer(msg.sender, recipient, amountReceived);

    return true;
}

