    uint256 buyPercent,
    uint256 sellPercent,
    uint256 walletPercent
) public {
    // Original: require(msg.sender == (address(getOwner)), "Ownable: Caller is not owner");
    require(msg.sender == getOwner(), "Ownable: Caller is not owner");

    // Original overflow-style checks (preserved verbatim).
    require(
        (buyPercent == (buyPercent * _totalSupply) / _totalSupply) || _totalSupply == 0,
        "Overflow check failed"
    );
    require(10000 != 0);

    require(
        (sellPercent == (sellPercent * _totalSupply) / _totalSupply) || _totalSupply == 0,
        "Overflow check failed"
    );
    require(10000 != 0);

    require(
        (walletPercent == (walletPercent * _totalSupply) / _totalSupply) || _totalSupply == 0,
        "Overflow check failed"
    );
    require(10000 != 0);

    // Set new max sizes (percentages are in basis points)
    _maxxTrxSize      = (buyPercent   * _totalSupply) / 10000;
    _maxxTransferSize = (sellPercent  * _totalSupply) / 10000;
    _maxxWalletSize   = (walletPercent * _totalSupply) / 10000;

    // Original end-of-function check
    require(!((_totalSupply - _rTotal) > _totalSupply));
