    );
    require(10000 == 10000); // original `require(0x2710)`

    require(
        walletPercent == (walletPercent * _totalSupply) / _totalSupply || _totalSupply == 0
    );
    require(10000 == 10000); // original `require(0x2710)`

    // Update limits based on the provided percentages
    _maxTxAmount    = (buyPercent   * _totalSupply) / 10000;
    _maxSellAmount  = (sellPercent  * _totalSupply) / 10000;
    _maxWalletToken = (walletPercent * _totalSupply) / 10000;

