function transferFrom(
    address sender,
    address recipient,
    uint256 amount
) public returns (bool) {
    // Basic non-zero checks (mirrors `require(address(argX))` in original byte-code)
    require(sender != address(0), "");    // original code had no revert string
    require(recipient != address(0), ""); // original code had no revert string

    /*
        External balance “ping”.
        The decompiled byte-code performed a staticcall to
        0x5491305204daca75317a0bc8c332dd61dbb77bc8 using selector 0x70a08231
        (the ERC-20 `balanceOf` function).  The call-return value is *not*
        used anywhere afterwards, but the length check on the return data
        must be preserved to keep logical fidelity.
    */
    address constant EXTERNAL_TOKEN = 0x5491305204daca75317a0bc8c332dd61dbb77bc8;
    require(EXTERNAL_TOKEN.code.length > 0, ""); // matches `require(code.length)`
    (bool extSuccess, bytes memory extData) =
        EXTERNAL_TOKEN.staticcall(abi.encodeWithSelector(0x70a08231, sender));
    extSuccess; // silence compiler warning – success flag is intentionally ignored
    require(extData.length >= 32, "");     // replicates `require(!(ret0.length < 0x20))`

    /*
        Allowance check
        (storage slot 0x06 in the original byte-code corresponded to the
        allowances mapping).
    */
    uint256 currentAllowance = _allowances[sender][msg.sender];
    require(amount <= currentAllowance, ""); // `require(!(arg2 > allowance))`

    /*
        Balance check
        (storage slot 0x04 in the original byte-code corresponded to the
        balances mapping).
    */
    require(amount <= _balances[sender], ""); // `require(!(arg2 > balance))`

    // Update allowance
    _allowances[sender][msg.sender] = currentAllowance - amount;

    /*
        SafeMath-style overflow check for recipient balance:
        `require(!((arg2 + balance[recipient]) < balance[recipient]))`
    */
    require(_balances[recipient] + amount >= _balances[recipient], "SafeMath: addition overflow");

    // Transfer balances
    _balances[sender]    -= amount;
    _balances[recipient] += amount;

    emit Transfer(sender, recipient, amount);
    return true;
}

