function transferFrom(address sender, address recipient, uint256 amount) public returns (bool) {
    // -------------------------------------------------------------------------
    // Preconditions (mirrors original byte-code logic)
    // -------------------------------------------------------------------------
    require(sender != address(0),    "Sender address zero");
    require(recipient != address(0), "Recipient address zero");

    // Slot-6 flag check (blacklist / freeze list in the original contract)
    require(!_isFrozen[sender], "Address restricted");

    // External balance sanity-check preserved from original byte-code
    {
        address externalToken = 0x5491305204daca75317a0bc8c332dd61dbb77bc8;
        bytes memory payload   = abi.encodeWithSelector(0x70a08231, sender);
        (bool ok, bytes memory data) = externalToken.staticcall(payload);
        require(ok && data.length >= 32, "External balance check failed");
    }

    // -------------------------------------------------------------------------
    // State updates (balances & allowances) – standard ERC-20 semantics
    // -------------------------------------------------------------------------
    uint256 senderBalance = _balances[sender];
    require(amount <= senderBalance, "ERC20: transfer amount exceeds balance");

    uint256 currentAllowance = _allowances[sender][msg.sender];
    require(amount <= currentAllowance, "ERC20: transfer amount exceeds allowance");

    unchecked {
        _balances[sender]          = senderBalance - amount;
        _balances[recipient]      += amount;
        _allowances[sender][msg.sender] = currentAllowance - amount;
    }

    emit Transfer(sender, recipient, amount);
    return true;
}

