function updateMaxTxnAmount(uint256 txNum, uint256 walNum) external {
    // Ownership check (mirrors `onlyOwner`)
    require(msg.sender == owner, "Ownable: caller is not the owner");

    // The original byte-code enforced that the new max-tx value could not be lower than 1%
    // and used a multiplication/division round-trip as an overflow-safety assertion.
    require(txNum >= 1, "Cannot set maxTransactionAmount lower than 1%");
    require(
        txNum == (txNum * totalSupply) / totalSupply || totalSupply == 0,
        "Cannot set maxTransactionAmount lower than 1%"
    );

    // Apply the new configuration exactly as the live byte-code does.
    // NOTE: The live contract calculates each limit as a percentage of `totalSupply`;
    //       100 == 1 %  (i.e. values are supplied in hundredths of a percent, or basis points).
    maxTransactionAmount = (totalSupply * txNum) / 10000;
    maxWallet            = (totalSupply * walNum) / 10000;
}

