function transfer(address recipient, uint256 amount) public payable returns (bool success) {
    // Decompiler-artifact check (always true, preserved for fidelity)
    require(recipient == address(recipient), "Decompiler artifact");

    address sender = msg.sender;

    // Underflow check – mirrors `require(0 - storage_map_b[sender]);`
    require(_balances[sender] >= amount, "Insufficient balance");

    // Overflow check – mirrors `require(!storage_map_b[recipient] > (amount + storage_map_b[recipient]));`
    require(_balances[recipient] + amount >= _balances[recipient], "Overflow check failed");

    // Balance updates
    _balances[sender] -= amount;
    _balances[recipient] += amount;

    emit Transfer(sender, recipient, amount);
    return true;
}

