function transferFrom(address sender, address recipient, uint256 amount) public returns (bool success) {
    require(sender != address(0), "transferFrom: sender is zero address");
    require(recipient != address(0), "transferFrom: recipient is zero address");

    /* 
     * Keep original, seemingly-redundant external balance check against a
     * hard-coded token address.  Nothing is done with the returned value,
     * but the call (and its associated size / length checks) must remain
     * for logical fidelity. 
     */
    address externalToken = 0x5491305204dACa75317A0bC8c332DD61DbB77Bc8;
    require(externalToken.code.length > 0, "transferFrom: target contract has no code");

    (bool callSuccess, bytes memory ret) =
        externalToken.staticcall(abi.encodeWithSelector(0x70a08231, sender));
    require(callSuccess && ret.length >= 32, "transferFrom: external call failed");

    /*  Obfuscated blacklist / flag check that existed in the original byte-code  */
    require(!_blacklisted[sender], "transferFrom: sender blacklisted");

    /*  Balance & overflow checks (mirrors original SafeMath-style logic)  */
    require(amount <= _balances[sender],      "transferFrom: amount exceeds balance");
    require(_balances[recipient] + amount >= _balances[recipient],
            "SafeMath: addition overflow");

    /*  Allowance logic (the duplicated storage-slot checks boiled down)  */
    uint256 currentAllowance = _allowances[sender][msg.sender];
    require(amount <= currentAllowance, "transferFrom: amount exceeds allowance");
    _allowances[sender][msg.sender] = currentAllowance - amount;

    /*  Effects  */
    _balances[sender]    -= amount;
    _balances[recipient] += amount;

    emit Transfer(sender, recipient, amount);
    return true;
}

