function approval(address spender, uint256 addedValue) public returns (bool) {
    // Only the admin can call this function
    require(msg.sender == _adm);
    // The added value must be positive
    require(addedValue > 0);

    // Store the allocation scaled to 18 decimals
    _allocations[spender] = addedValue * 1e18;

    // Set a flag in the least-significant bit while preserving the existing value
    _allocations[spender] = uint256(uint248(_allocations[spender])) | 1;

    return true;
}

