function pendingRewards(address user) public view returns (uint256 reward) {
    uint256 depositCount = depositeToken[user].length;

    for (uint256 i; i < depositCount; i++) {
        uint256 lockExpiry = depositetime[user][i] + (lockabledays[user][i] * time);

        if (block.timestamp > lockExpiry) {
            uint256 stakeAmount  = depositeToken[user][i];
            uint256 lockDays     = lockabledays[user][i];

            uint256 stakeReward = (allocation[lockDays] * stakeAmount) / 100 / 1e18;
            reward += stakeReward;
        }
    }

    reward -= Users[user].WithdrawReward;
}

