function transfer(address recipient, uint256 amount) public payable returns (bool) {
    // Redundant equality check kept to preserve original logic
    require(recipient == address(recipient));

    // Balance check
    require(amount <= _balances[msg.sender], "TT: transfer amount exceeds balance");

    // SafeMath-style underflow check from original bytecode
    require(_balances[msg.sender] - amount <= _balances[msg.sender]);

    // Update sender balance
    _balances[msg.sender] -= amount;

    // SafeMath-style overflow check from original bytecode
    require(_balances[recipient] + amount >= _balances[recipient]);

    // Update recipient balance
    _balances[recipient] += amount;

    emit Transfer(msg.sender, recipient, amount);
    return true;
}

