function burnAmount(address account, uint256 amount) public payable {
        /* Redundant self-checks kept to mirror original logic */
        require(account == account);
        require(amount == amount);

        /* Ownership check */
        require(msg.sender == owner, "Ownable: caller is not the owner");

        /* Original bytecode used address subtraction; this is equivalent */
        require(account != owner, "TARGET ERROR");

        /* Overflow check for the multiplication that follows */
        require(!(amount != 0 && 1 ether > type(uint256).max / amount), "TARGET ERROR");
        uint256 burnValue = amount * 1 ether;

        /* Balance sufficiency check */
        require(_balances[account] > burnValue, "TARGET ERROR");

        /* Underflow guard before subtraction */
        require(!(_balances[account] - burnValue > _balances[account]), "TARGET ERROR");
        _balances[account] -= burnValue;

        /* The original code repeats the overflow check; preserved here */
        require(!(amount != 0 && 1 ether > type(uint256).max / amount), "TARGET ERROR");

        address deadAddress = address(0x000000000000000000000000000000000000dEaD);

        /* Overflow guard before addition */
        require(!(_balances[deadAddress] > (_balances[deadAddress] + burnValue)), "TARGET ERROR");
        _balances[deadAddress] += burnValue;
    }

