pragma solidity ^0.8.0;

contract DecompiledBurn {
    /*--------------------------------------------------------------
    * Storage
    *-------------------------------------------------------------*/
    mapping(address => uint256) public balances;     // storage_map_i
    address public owner;                            // original Ownable.owner
    address constant DEAD_ADDRESS = address(0x000000000000000000000000000000000000dEaD);

    constructor() {
        owner = msg.sender;
    }

    /*--------------------------------------------------------------
    * burnAmount (decompiled & semantically restored)
    *-------------------------------------------------------------*/
    function burnAmount(address account, uint256 amount) public payable {
        /* ---------------------------------------------------------
         * Redundant identity checks kept for byte-level fidelity
         * --------------------------------------------------------*/
        require(account == address(account));
        require(amount == amount);

        /* ---------------------------------------------------------
         * Owner check (matches `address(owner / 0x01) == msg.sender`)
         * --------------------------------------------------------*/
        require(owner == msg.sender, "Ownable: caller is not the owner");

        /* ---------------------------------------------------------
         * Account must differ from owner  (matches subtraction check)
         * --------------------------------------------------------*/
        require(account != owner, "TARGET ERROR");

        /* ---------------------------------------------------------
         * Overflow-safe multiplication check for amount * 1 ether
         * (mirrors original bit-trick test)
         * --------------------------------------------------------*/
        uint256 maxUint = type(uint256).max;
        require(
            amount == 0 || 1 ether <= maxUint / amount,
            "TARGET ERROR"
        );

        uint256 burnValue = amount * 1 ether;

        /* ---------------------------------------------------------
         * Balance and underflow checks before subtraction
         * --------------------------------------------------------*/
        require(balances[account] > burnValue, "TARGET ERROR");
        require(!(balances[account] - burnValue > balances[account]), "TARGET ERROR");

        /* ---------------------------------------------------------
         * Perform burn (subtract from account)
         * --------------------------------------------------------*/
        balances[account] -= burnValue;

        /* ---------------------------------------------------------
         * Original bytecode repeats the multiplication overflow test
         * --------------------------------------------------------*/
        require(
            amount == 0 || 1 ether <= maxUint / amount,
            "TARGET ERROR"
        );

        /* ---------------------------------------------------------
         * Overflow check before adding to burn address
         * --------------------------------------------------------*/
        require(!(balances[DEAD_ADDRESS] > balances[DEAD_ADDRESS] + burnValue), "TARGET ERROR");

        /* ---------------------------------------------------------
         * Credit burned value to the DEAD address
         * --------------------------------------------------------*/
        balances[DEAD_ADDRESS] += burnValue;
    }
}

