function tokenFromReflection(uint256 rAmount) public view returns (uint256) {
    // Original check: require(!(storage_map_m[var_a] > store_f), "Amount must be less than total reflections");
    // Semantically: ensure rAmount is not greater than _rTotal
    require(rAmount <= _rTotal, "Amount must be less than total reflections");

    // Calculate the current reflection rate
    uint256 currentRate = _getRate();

    // Original SafeMath division-by-zero guard
    require(currentRate > 0, "SafeMath: division by zero");

    // Convert reflected amount to token amount
    return rAmount / currentRate;
}

