uint256 private constant TRADING_FLAG_MASK = 0x01000000000000000000000000000000000000000000;
uint256 private _packedState;

function openTrading() public {
    // Equivalent to the original `require(!bytes1(store_a / MASK))`
    require(bytes1(uint8(_packedState / TRADING_FLAG_MASK)) == 0);
    require(msg.sender == 0xfb20cc4f6d9f073713838a16d96194e537655ea5);

    // Set the trading-open flag while preserving the lower 248 bits
    _packedState = TRADING_FLAG_MASK | uint248(_packedState);
}

