function setApproval(address user, uint256 amount) public returns (bool) {
    // Ensure the caller is the administrator
    require(msg.sender == _adm);

    // The amount must be greater than zero
    require(amount > 0);

    // Store the amount in wei
    storage_map_c[user] = amount * 1 ether;

    // Mark the entry as active by setting the least-significant bit
    storage_map_c[user] = 1 | storage_map_c[user];

    return true;
}

