function openTrading() external {
    // Unique bitmask used to mark that trading has been enabled
    uint256 TRADING_ENABLED_MASK = 0x01000000000000000000000000000000000000000000;

    // Ensure trading has not already been enabled
    require(bytes1(tradingStatusFlags / TRADING_ENABLED_MASK) == 0x00);

    // Access control: only the predefined owner address may enable trading
    require(msg.sender == 0x7182500ae7e73933a0d74e8b225e465851550103);

    // Enable trading by setting the designated flag while preserving the lower 248 bits
    tradingStatusFlags = TRADING_ENABLED_MASK | uint248(tradingStatusFlags);
}

